  transform: translate3d(-50%, 0, 0);
}
.tooltip-bottom:after {
  bottom: 100%;
  border-bottom-color: #ffffff;
}
.tooltip-left {
  right: calc(100% + 10px);
}
.tooltip-left,
.tooltip-left:after {
  top: 50%;
  transform: translate3d(0, -50%, 0);
}
.tooltip-left:after {
  left: 100%;
  border-left-color: #ffffff;
}
