@keyframes scale-in-center {
  0% {
    transform: scale(0.5);
    opacity: 0;
  }
  100% {
    transform: scale(1);
    opacity: 1;
  }
}
.root.enter {
  animation: scale-in-center 0.4s cubic-bezier(0.39, 0.575, 0.565, 1) both;
}
.root.leave {
  animation: scale-in-center 0.4s cubic-bezier(0.39, 0.575, 0.565, 1) both reverse;
}
@keyframes shake {
  100% {
    transform: none;
  }
  0%,
  99% {
    transform: translate(0, 0);
  }
  20% {
    transform: translateX(-10px);
  }
  60% {
    transform: translateX(-5px);
  }
  40% {
    transform: translateX(10px);
  }
  80% {
    transform: translateX(5px);
  }
}
.form.submit .invalid {
  animation: shake 600ms;
}
@keyframes sent-backdrop {
  0%,
  100% {
    opacity: 0;
    background-color: rgba(1, 1, 57, 0);
  }
  50%,
  75% {
    opacity: 0.6;
    background-color: #010139;
  }
}
@keyframes success-icon-scale {
  0% {
    opacity: 0;
    transform: translate(-50%, -50%) scale(0.8);
  }
  50% {
    opacity: 1;
    transform: translate(-50%, -50%) scale(1);
  }
  75% {
    opacity: 1;
    transform: translate(-50%, -50%) scale(1);
  }
  100% {
    opacity: 0;
    transform: translate(-50%, -50%) scale(0.2);
  }
}
@keyframes success-icon-show-path {
  0% {
    opacity: 0;
  }
  50% {
    opacity: 0;
  }
  100% {
    opacity: 1;
  }
}
@keyframes success-icon-draw-circle {
  0% {
    opacity: 1;
    stroke-dashoffset: 150.72;
  }
  25% {
    opacity: 1;
    stroke-dashoffset: 75.36;
  }
  50% {
    opacity: 1;
    stroke-dashoffset: 0;
  }
  100% {
    opacity: 0;
    stroke-dashoffset: 0;
  }
}
@keyframes success-icon-draw-check {
  0% {
    opacity: 1;
    stroke-dashoffset: 36;
  }
  25% {
    opacity: 1;
    stroke-dashoffset: 18;
  }
  50% {
    opacity: 1;
    stroke-dashoffset: 0;
  }
  100% {
    opacity: 0;
    stroke-dashoffset: 0;
  }
}
